import React, { useEffect, useState } from "react";
import axios from "axios";
const Summary = () => {
  const [response, setResponse] = useState([]);
  useEffect(() => {
    //Axios call
     axios.get("http://localhost:5000/summary").then((res) => {
       console.log("successfully received ",res  );
       setResponse(res.data);
     });
  }, []);
  return (
    <div className="Summary">
      <h4>Summary of Feedback</h4>
      {console.log("Summary Response ",response )}
      {response.map((item, index) => (
        <div key={item._id}>
          {/* <label>Name:</label>
          <input type="text" value={item.username} />
          <br />
          <label>Course Name:</label>
          <input type="text" value={item.coursename} />
          <br />
          <label>Rating:</label>
          <input type="text" value={item.rating} />
          <br />
          <label>Comments:</label>
          <input type="text" value={item.comments} /> */
          <table style={{width:"100%" ,border:"1"}} >
  <tr>
    <th>Username</th>
    <th>Coursename</th> 
    <th>Rating</th>
    <th>Comments</th>
  </tr>
  <tr>
    <td>{item.username}</td>
    <td>{item.coursername}</td>
    <td>{item.rating}</td>
    <td>{item.comments}</td>
  </tr>
  
</table>
          }
        </div>
      ))}
    </div>
  );
};

export default Summary;
