import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import axios from "axios";

const Comments = (props) => {
  const [comments, setComments] = useState("");
  const history = useHistory();
  const handleSubmit = () => {
    axios
      .post("http://localhost:5000/feedback", {
        username: props.name,
        coursename: props.course,
        rating: props.rating,
        comments,
      })
      .then((res) => {
        console.log("successfully posted");
        history.push("/summary");
      });
  };
  return (
    <div className="comments">
      {console.log(props.rating.rating)}
      {console.log(props.username)}
      {console.log(props.course)}
      <div className="confirm__container">
        <h4>Please provide additional comments</h4>
        <label>Name:</label>
        <input style={{
                  fontSize: "15px",
                  fontFamily: "Cursive,sans-serif,Gugi",
                  borderRadius: "10px",
                  marginLeft: "50px",
                  marginTop: "15px",
                }} type="text" value={props.name} />
        <br />
        <label style = {{  textalign:"right",
  paddingright:"20px",
  display:"inline-block",
  minwidth:"150px"}}>Course Name:</label>
        <input type="text" value={props.course} />
        <br />
        <label>Rating:</label>
        <input type="text" value={props.rating.rating} />
        <br />
        <label>Additional Comments:</label>
        <input
          type="text"
          value={comments}
          onChange={(e) => setComments(e.target.value)}
        />
        <br />
        {/* <Link to="/summary"> */}
        <input type="submit" onClick={handleSubmit} value="Submit" />
        {/* </Link> */}
      </div>
    </div>
  );
};

export default Comments;
